// App.js
import React, { useEffect, useRef } from 'react';
import './App.css';

function App() {
  const titleRef = useRef(null);
  const phraseRef = useRef(null);

  useEffect(() => {
    const title = titleRef.current;
    const phrase = phraseRef.current;

    title.style.transform = 'translateX(100%)';
    phrase.style.transform = 'translateX(-100%)';

    setTimeout(() => {
      title.style.transform = 'translateX(0)';
      phrase.style.transform = 'translateX(0)';
    }, 1000);
  }, []);

  return (
    <div className="container">
      <div className="content">
        <h1 ref={titleRef} className="title">Transport Num√©rique</h1>
        <div ref={phraseRef} className="phrase">
          <p className="description">Voyagez vers demain avec nous</p>
        </div>
        <div className="links">
          <a href="#" className="continue-link">Cliquez ici pour continuer</a>
        </div>
      </div>
    </div>
  );
}

export default App;
